ALTER TABLE coffeeshop_sales
ADD locations_count NUMBER;

UPDATE coffeeshop_sales
SET locations_count = 
    CASE
        WHEN branch_locations IS NULL THEN 1
        ELSE LENGTH(branch_locations) - LENGTH(REPLACE(branch_locations, ',', '')) + 2
    END;
    


