-- 1. c_coffeeshop
CREATE TABLE c_coffeeshops (
    coffeeshop_id NUMBER PRIMARY KEY,
    coffeeshop_name VARCHAR2(255),
    branch_locations VARCHAR2(255),
    has_other_branches VARCHAR2(20),
    most_popular_branch VARCHAR2(255),
    has_loyalty_card VARCHAR2(20)
);


INSERT INTO c_coffeeshops (
    coffeeshop_id,
    coffeeshop_name,
    branch_locations,
    has_other_branches,
    most_popular_branch,
    has_loyalty_card
)
SELECT
    coffeeshop_id,
    coffeeshop_name,
    branch_locations,
    has_other_branches,
    most_popular_branch,
    has_loyalty_card
FROM (
    SELECT 
        coffeeshop_id,
        coffeeshop_name,
        branch_locations,
        has_other_branches,
        most_popular_branch,
        has_loyalty_card,
        ROW_NUMBER() OVER (PARTITION BY coffeeshop_name ORDER BY ROWNUM) AS rn
    FROM coffeeshop_sales
)
WHERE rn = 1;


