    c_wolt_prices wp 
    ON t.product_id = wp.product_id
    AND wp.coffeeshop_id = b.coffeeshop_id
WHERE 
    t.isflagwolt IN ('YES', 'NO')
    AND (pp.price IS NOT NULL OR wp.price IS NOT NULL);



